  const [porta2, setPorta2] = useState('');
  const [largura3, setLargura3] = useState('');
  const [altura3, setAltura3] = useState('');
  const [janela3, setJanela3] = useState('');
  const [porta3, setPorta3] = useState('');
  const [largura4, setLargura4] = useState('');
  const [altura4, setAltura4] = useState('');
  const [janela4, setJanela4] = useState('');
  const [porta4, setPorta4] = useState('');
  const [inputError, setInputError] = useState('');
  const [totalLarg, setTotalLarg] = useState('');
  const [totalAlt, setTotalAlt]= useState('');
  const [totalJan, setTotalJan]= useState('');
  const [totalPort, setTotalPort] = useState('')

  function handleAdd() {
      setTotalLarg(largura1+largura2+largura3+largura4);
      console.log(totalLarg)

  }

