import styled from 'styled-components'
import { shade } from 'polished'

export const Title = styled.h1`
font-size: 38px;
color: #ffffff;
margin-top: 80px;
max-width: 800px;

`;

export const Form = styled.form `
margin-top: 40px;
max-width: 800px;
display:flex;

input{
    flex:1;
    height: 50px;
    padding: 0 24px;
    border-radius: 10px;
}
button {
    width: 210px;
    height: 50px;
    background: #04d361;
    color: #fff;

    &:hover{
        background: ${shade(0.2, '#04d361')};
    }
}

`;
