  const [porta4, setPorta4] = useState('');
  const [inputError, setInputError] = useState('');

  function handleAdd() {
    console.log(largura1, 'largura');
    console.log(altura1, 'altura');
    console.log(janela1, 'janela');
    console.log(porta1, 'porta');
  }

  return (
    <>
      <Title>
        Calcule quantas tintas vocÃª precisa para pintar a sua parede
      </Title>
      <Text> Parede 1</Text>
      <Form>
        <input
          placeholder="Largura"
          value={largura1}
          onChange={e => setLargura1(e.target.value )}
        />
        <input
          placeholder="Altura"
          value={altura1}
          onChange={e => setAltura1(e.target.value )}
        />
      </Form>
      <CheckBox>
        <input type="checkbox" id="porta" name="porta" />
        <label>Tem porta?</label>
        <input
          placeholder="Quantidade de portas"
          value={porta1}
          onChange={e => setPorta1(e.target.value )}
        />
        <input type="checkbox" id="janela" name="Janela" />
        <label>Tem janela?</label>
        <input
          placeholder="Quantidade de janelas"
          value={janela1}
          onChange={e => setJanela1(e.target.value)}
        />
      </CheckBox>
      <Text> Parede 2</Text>
      <Form>
        <input
          placeholder="Largura"
          value={largura2}
          onChange={e => setLargura2(e.target.value)}
        />
        <input
          placeholder="Altura"
          value={altura2}
          onChange={e => setAltura2(e.target.value)}
        />
      </Form>
      <CheckBox>
        <input type="checkbox" id="porta" name="porta" />
        <label>Tem porta?</label>
        <input
          placeholder="Quantidade de portas"
          value={porta2}
          onChange={e => setPorta2(e.target.value)}
        />
        <input type="checkbox" id="janela" name="Janela" />
        <label>Tem janela?</label>
        <input
          placeholder="Quantidade de janelas"
          value={janela2}
          onChange={e => setJanela2(e.target.value)}
        />
      </CheckBox>
      <Text> Parede 3</Text>
      <Form>
        <input
          placeholder="Largura"
          value={largura3}
          onChange={e => setLargura3(e.target.value)}
        />
        <input
          placeholder="Altura"
          value={altura3}
          onChange={e => setAltura3(e.target.value)}
        />
      </Form>
      <CheckBox>
        <input type="checkbox" id="porta" name="porta" />
        <label>Tem porta?</label>
        <input
          placeholder="Quantidade de portas"
          value={porta3}
          onChange={e => setPorta3(e.target.value)}
        />
        <input type="checkbox" id="janela" name="Janela" />
        <label>Tem janela?</label>
        <input
          placeholder="Quantidade de janelas"
          value={janela3}
          onChange={e => setJanela3(e.target.value)}
        />
      </CheckBox>
      <Text> Parede 4</Text>
      <Form>
        <input
          placeholder="Largura"
          value={largura4}
          onChange={e => setLargura4(e.target.value)}
        />
        <input
          placeholder="Altura"
          value={altura4}
          onChange={e => setAltura4(e.target.value)}
        />
      </Form>
      <CheckBox>
        <input type="checkbox" id="porta" name="porta" />
        <label>Tem porta?</label>
        <input
          placeholder="Quantidade de portas"
          value={porta4}
          onChange={e => setPorta4(e.target.value)}
        />
        <input type="checkbox" id="janela" name="Janela" />
        <label>Tem janela?</label>
        <input
          placeholder="Quantidade de janelas"
          value={janela4}
          onChange={e => setJanela4(e.target.value)}
        />
      </CheckBox>
      <Form onSubmit={handleAdd}>
        <button type="submit">Calcular</button>
      </Form>
    </>
  );
};
export default Dashboard;
